export default function FeaturedVideos() {
  const scrollRef = useRef(null);
  const [activeIndex, setActiveIndex] = useState(null);

  // Scroll outside container
  const scroll = (dir) => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({
        left: dir === "left" ? -300 : 300,
        behavior: "smooth",
      });
    }
  };

  // Mouse wheel horizontal scroll
  const handleWheel = (e) => {
    if (scrollRef.current) {
      e.preventDefault();
      scrollRef.current.scrollLeft += e.deltaY;
    }
  };

  // Keyboard navigation inside modal
  useEffect(() => {
    const handleKey = (e) => {
      if (activeIndex !== null) {
        if (e.key === "ArrowRight") {
          setActiveIndex((prev) => (prev + 1) % videos.length);
        } else if (e.key === "ArrowLeft") {
          setActiveIndex((prev) => (prev - 1 + videos.length) % videos.length);
        } else if (e.key === "Escape") {
          setActiveIndex(null);
        }
      }
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [activeIndex]);

  // Swipe gesture support
  let startX = 0;
  const handleTouchStart = (e) => {
    startX = e.touches[0].clientX;
  };
  const handleTouchEnd = (e) => {
    const diff = e.changedTouches[0].clientX - startX;
    if (Math.abs(diff) > 50) {
      if (diff < 0) {
        setActiveIndex((prev) => (prev + 1) % videos.length); // swipe left → next
      } else {
        setActiveIndex((prev) => (prev - 1 + videos.length) % videos.length); // swipe right → prev
      }
    }
  };

  return (
    <div className="featured-container">
      <h2>Featured in videos</h2>
      <p className="subtitle">See what creators are sharing</p>

      <div className="video-wrapper">
        {/* Left Button */}
        <button className="scroll-btn left" onClick={() => scroll("left")}>
          <FaChevronLeft />
        </button>

        {/* Scrollable videos */}
        <div className="video-scroll" ref={scrollRef} onWheel={handleWheel}>
          {videos.map((video, index) => (
            <div
              className="video-card"
              key={video.id}
              onClick={() => setActiveIndex(index)}
            >
              <div className="video-thumb">
                <img src={video.thumb} alt={video.title} />
                <span className="user">{video.user}</span>
              </div>
              <div className="video-info">
                <p className="price">{video.price}</p>
                <p className="title">{video.title}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Right Button */}
        <button className="scroll-btn right" onClick={() => scroll("right")}>
          <FaChevronRight />
        </button>
      </div>

      {/* Modal with swipe + keyboard + buttons */}
      {activeIndex !== null && (
        <div
          className="video-modal"
          onTouchStart={handleTouchStart}
          onTouchEnd={handleTouchEnd}
        >
          <button className="close-btn" onClick={() => setActiveIndex(null)}>
            <FaTimes />
          </button>

          <video
            src={videos[activeIndex].src}
            controls
            autoPlay
            className="modal-video"
          />

          <div className="modal-info">
            <p>{videos[activeIndex].user}</p>
            <p>{videos[activeIndex].title}</p>
            <strong>{videos[activeIndex].price}</strong>
          </div>

          {/* Prev / Next inside modal */}
          <button
            className="modal-nav left"
            onClick={() =>
              setActiveIndex((prev) => (prev - 1 + videos.length) % videos.length)
            }
          >
            <FaChevronLeft />
          </button>
          <button
            className="modal-nav right"
            onClick={() =>
              setActiveIndex((prev) => (prev + 1) % videos.length)
            }
          >
            <FaChevronRight />
          </button>
        </div>
      )}
    </div>
  );
}
