import React, { useRef } from "react";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
//import { addToCart } from "../features/cart/cartSlice";
import { setSelectedProduct } from "../features/cart/productSlice"; //
import "./Product.css"

const products = [
  {
    id: 1,
    title: "Halloween Sweatshirt",
    price: "$18.39",
    oldPrice: "$21.99",
    img: "data:image/jpeg;base64,",
  },
  {
    id: 2,
    title: "Pink Halloween Blanket",
    price: "$12.45",
    oldPrice: "$13.99",
    img: "data:image/jpeg;base64,",
  },
  {
    id: 3,
    title: "Halloween Tumbler",
    price: "$27.99",
    img: "data:image/jpeg;base64,",
  },
  {
    id: 4,
    title: "Ghost Plush Pillow",
    price: "$12.99",
    img: "data:image/jpeg;base64,",
  },
  {
    id: 5,
    title: "Pumpkin Ghost Figurine",
    price: "$7.80",
    img: "data:image/jpeg;base64,",
  },
  {
    id: 6,
    title: "Skeleton Candle",
    price: "$18.90",
    img: "data:image/jpeg;base64,",
  },
];

 function HalloweenScroll() {
  const scrollRef = useRef(null);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  
  const handleProductClick = (item) => {
      dispatch(
        setSelectedProduct({
      id: item.id,
      name: item.title,
      image: item.img,
      price: parseFloat(item.price.replace("$", "")),  // convert string → number
      oldPrice: item.oldPrice
        ? parseFloat(item.oldPrice.replace("$", ""))
        : null,
      quantity: 1,
      brand: item.brand || "Halloween Special ",
      seller: item.seller || "Official LHRIVER Store",
      colors: item.colors || ["Orange", "Black"],

      // Details matching Halloween theme
      details: item.details || [
        " Spooky vibes included",
        " Limited Halloween Edition",
      ],

      // Example specs (you can extend based on real product)
      specs: item.specs || {
        theme: "Halloween 2025",
        material: "Pumpkin Fiber",
        special: "Glow in the Dark",
      },
    })
      );
      navigate("/checkout");
    };
  

  const handleWheel = (e) => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft += e.deltaY;
    }
  };

  const scroll = (direction) => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft += direction === "left" ? -300 : 300;
    }
  };

  return (
    <div className="scroll-container">
      <h2 className="title">All the Halloween feels 🎃</h2>

      <div className="scroll-wrapper">
        <button className="scroll-btn left" onClick={() => scroll("left")}>
          <FaChevronLeft size={20} />
        </button>

        <div className="scroll-items" ref={scrollRef} onWheel={handleWheel}>
          {products.map((item) => (
            <div
              key={item.id}
              className="card"
              onClick={() => handleProductClick(item)}
            >
             <img
  src={
    item.img ||
    (item.img && item.img.length > 0 ? item.img[0].url : null)
  }
  alt={item.name || "Product"}
  onError={(e) => {
    e.target.src = "https://via.placeholder.com/300x300?text=No+Image";
  }}
/>
              <div className="info">
                <p className="title-text">{item.title}</p>
                <p className="price">{item.price}</p>
                {item.oldPrice && (
                  <p className="old-price">{item.oldPrice}</p>
                )}
              </div>
            </div>
          ))}
        </div>

        <button className="scroll-btn right" onClick={() => scroll("right")}>
          <FaChevronRight size={20} />
        </button>
      </div>
    </div>
  );
}
export default HalloweenScroll;